mapper.insert:
INSERT INTO drivers_info
VALUES (
	null
	, #{dri_name}
	, CONCAT(#{dri_tel1}, '-', #{dri_tel2}, '-', #{dri_tel3})
	, #{dri_birthday}
	, CONCAT(#{lic_num1}, '-', #{lic_num2}, '-', #{lic_num3}, '-', #{lic_num4})
	, #{lic_info}
	, #{lic_issue_date}
	, #{lic_expiration_date}
	, #{lic_num1}
)

mapper.select:
SELECT mem_idx
	FROM mem_info
		WHERE mem_id = #{id}

mapper.insert:
INSERT INTO res_info
VALUES (
