	, #{res_rental_date}
	, #{res_return_date}
	, #{brc_rent_name}
	, #{brc_return_name}
	, #{car_idx}
	, #{car_insurance}
	, CONCAT(#{lic_num1}, '-', #{lic_num2}, '-', #{lic_num3}, '-', #{lic_num4})
	, #{mem_idx}
)

INSERT INTO pay_info
VALUES (
	null
	, #{res_idx}
	, #{payment.pay_method}
	, #{payment.pay_total}
	, #{payment.pay_status}
	, now()
	, #{payment.merchant_uid}
)

mapper.select:
SELECT pay_total
	FROM pay_info
		WHERE res_idx = #{res_idx};
