mapper.insert:
insert into
	car_info
values (
	null
	, #{car_type}
	, #{car_number}
	, #{car_company}
	, #{car_model}
	, #{car_old}
	, #{car_shift_type}
	, #{car_fuel_type}
	, #{car_riding}
	, #{car_age}
	, #{car_career}
	, #{car_license}
	, #{car_weekdays}
	, #{car_weekend}
	, #{brc_name}
	, #{car_file}
	, '신규'
	, #{car_file_path}
	, now()
)

mapper.select:
select
	*
from
	car_info


		where car_number=#{car_number}


		where car_idx=#{car_idx}

mapper.update:
update
	option_list
set


			option_name = #{option_name}


			option_name = #{option_name} 
			, option_image_url = #{option_image_url}


where
	option_idx = #{option_idx}
